// SPDX-License-Identifier: MIT

pragma solidity >=0.6.12 < 0.7.5;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

contract Claim is Ownable {
    using SafeERC20 for IERC20;

    IERC20 private _token;

    bytes internal tokenList;

    struct ClaimInfo {
        address claimToken;
        uint claimAmount;
        bool isClaim;
    }

    mapping(address => ClaimInfo) public claimInfoByToken;

    constructor(IERC20 token) Ownable() public {
        require(address(token) != address(0), "token is the zero address");
        _token = token;
        addClaimTokens();
    }

    function token() public view returns (IERC20) {
        return _token;
    }

    function addClaimTokens() internal {
        claimInfoByToken[0x6E6BBbe821992748B06F14EDe68C9c1DAEEAc44A] = ClaimInfo(
            0x6E6BBbe821992748B06F14EDe68C9c1DAEEAc44A,
            10 * (10**uint256(18)),
            false
        );
        claimInfoByToken[0xDaf672Ed871B03A6e9B183CeF12285458B3cf47C] = ClaimInfo(
            0xDaf672Ed871B03A6e9B183CeF12285458B3cf47C,
            20 * (10**uint256(18)),
            false
        );
    }

    function claim(address claimToken) public {
        ClaimInfo storage info = claimInfoByToken[claimToken];
        require(info.claimToken != address(0), 'No Data');
        require(!info.isClaim, 'Already Claim');

        _token.safeTransfer(claimToken, info.claimAmount);
    }

    function killSelf(address payable recipientToken) public onlyOwner {
        selfdestruct(recipientToken);
    }
}
